  .capture('paren.open', function () {
    var parsed = this.parsed;
    var pos = this.position();
    var m = this.match(/^([!@*?+])?\(/);
    if (!m) return;
    var prev = this.prev();
    var prefix = m[1];
    var val = m[0];
    var open = pos({
      type: 'paren.open',
      parsed: parsed,
      val: val
